import React, { useState } from 'react';

function App() {
	const [text, setText] = useState('Kossie'); 

	const onSubmit = () => {
		alert('hello');
	}

	const onKeyUp = (event) => {
		if (event.keyCode === 13) {
			onSubmit();
		}
	}

	// let text = 'Kossie';

	const updateText = () => {
		// text = 'Coder';
		setText('Coder');
		console.log(text);
	}

	return (
		<div className="App">
			<input onKeyUp={onKeyUp} />
			<button onClick={onSubmit}>Sumit</button>

			<br/>
			<br/>

			<span>{text}</span>
			<button onClick={updateText}>Update</button>
		</div>
	);
}

export default App;
