import React, { useState, useEffect } from 'react';

function App() {
	const [condition, setCondition] = useState(false);
	const toggole = () => setCondition(!condition);
	useEffect(() => {
		console.log(condition);
	}, [condition])
	const renderCondition = condition
	? <div>True</div>
	: <div>False</div>

	return (
		<div className="App">
			<h1>Kossie Coder</h1>
			<div>
				{renderCondition}
			</div>
			<button onClick={toggole}>toggle</button>
		</div>
	);

}

export default App;
